mapper.select:
SELECT INFO.ID, INFO.TRANSACTION_NAME, INFO.STATUS_DESC, INFO.TRANSACTION_SCHEME, INFO.TRANSACTION_FINISH, INFO.TRANSACTION_BEGIN, DEP.DEP_NAME, INFO.CREATE_TIME, INFO.UPDATE_TIME, INFO.STATUS, INFO.TRANSACTION_CONTENT FROM gzusu_manager_transaction_info INFO
WHERE INFO.TRANSACTION_NAME LIKE CONCAT('%',#{tName,jdbcType=VARCHAR},'%')
LEFT JOIN( SELECT DEP.DEP_NAME, MID.TRANSACTION_ID FROM gzusu_manager_department_info DEP LEFT JOIN gzusu_manager_transaction_department_mid MID ON MID.DEPARTMENT_ID = DEP.ID ) DEP ON INFO.ID = DEP.TRANSACTION_ID order by INFO.UPDATE_TIME DESC LIMIT #{startNum},#{size}

SELECT COUNT(d.ID) FROM ( SELECT * FROM gzusu_manager_transaction_info INFO
WHERE INFO.TRANSACTION_NAME LIKE CONCAT('%',#{tName,jdbcType=VARCHAR},'%')
)d
